beforeSaveCustomScript:
// Find the latest incoming webhook related to the current record
$latestWebhookId = record\findRelatedOne(
    'CLigacoesIA',                    
    id,                              
    'incomingWebhooks',              
    'createdAt',                     
    'desc'                           
);

// Get the payload from the latest webhook
$payload = ifThen(
    $latestWebhookId,
    record\attribute('IncomingWebhook', $latestWebhookId, 'payload')
);

// Only proceed if we have a valid payload
$callStatus = ifThen(
    $payload && object\has($payload, "call") && object\has($payload["call"], "call_status"),
    $payload["call"]["call_status"]
);

// Map call_status to your enum values
$mappedStatus = ifThenElse(
    $callStatus == "ongoing",
    "Em Progresso",
    ifThenElse(
        $callStatus == "ended",
        "Finalizada",
        ifThenElse(
            $callStatus == "ringing",
            "Ligando",
            ifThenElse(
                $callStatus == "rejected",
                "Recusada",
                ifThenElse(
                    $callStatus == "cancelled",
                    "Cancelada",
                    status  // Keep current status for unknown values
                )
            )
        )
    )
);

// Safe transcript extraction
$transcriptValue = ifThen(
    $payload && object\has($payload, "call") && object\has($payload["call"], "transcript"),
    $payload["call"]["transcript"]
);

// Safe call cost extraction - CONVERT FROM CENTS TO DOLLARS
$callCostValue = ifThen(
    $payload && 
    object\has($payload, "call") && 
    object\has($payload["call"], "call_cost") && 
    object\has($payload["call"]["call_cost"], "combined_cost"),
    number\round($payload["call"]["call_cost"]["combined_cost"] / 100, 4)
);

// Safe recording URL extraction
$recordingUrlValue = ifThen(
    $payload && object\has($payload, "call") && object\has($payload["call"], "recording_url"),
    $payload["call"]["recording_url"]
);

// Set your formula results
name = string\concatenate("Ligação de Feedback - Consulta - ", consultaMedica.medicoName," <> ",consultaMedica.pacienteName);
transcript = $transcriptValue;
status = $mappedStatus;
callCost = $callCostValue;
recordingUrl = $recordingUrlValue;
